    return function(num) {
      var result;
      var numToString = num + '';
      var dotNum = numToString.indexOf(".");
      if (dotNum == -1) {//无小数点
        result = numToString + ".00";
      } else {//有小数点
        result = numToString.substring(0, dotNum + 3);
      }
      return result;
    }
